ref_size = 80;i = 1: ref_size;sigma = 20;f = 1 / (sqrt(2*pi)* sigma) *exp(-(i -ref_size/2).^2 / (2* sigma^2));maximum = max(f);f = 0.15 / maximum * f;plot(1:ref_size, f)function [beta] = variable_para(ref_size,beta,idx)    sigma = 10;    i = 1: ref_size;    f = 1 / (sqrt(2*pi)* sigma) *exp(-(i -ref_size/2).^2 / (2* sigma^2));    maximum = max(f);    f = beta / maximum * f;    beta = f(idx);end