ref_size = 60;i = 1: ref_size;sigma = 10;f = 1 / (sqrt(2*pi)* sigma) *exp(-(i -ref_size/2).^2 / (2* sigma^2));maximum = max(f);f = 1 / maximum * f;figureplot(1:ref_size, f,'Linewidth',2)xlabel("distance(m)")ylabel("parameter weight")function [beta] = variable_para(ref_size,beta,idx)    sigma = 10;    i = 1: ref_size;    f = 1 / (sqrt(2*pi)* sigma) *exp(-(i -ref_size/2).^2 / (2* sigma^2));    maximum = max(f);    f = beta / maximum * f;    beta = f(idx);end